import {createSelector} from 'reselect';

const COLLECTION_ID_MAP = {
    javascript: 1,
    python: 2,
    php: 3,
    webDevelopment: 4,
    machineLearning: 5
}

const selectShop = state => state.shop;

const shopSelectorCollections= createSelector(
    [selectShop],
    shop => shop.collections    
);

export default shopSelectorCollections;
