import React from 'react'
import StripeCheckout from 'react-stripe-checkout';
import axios from 'axios'

const StripeCheckoutButton = ({price}) => {
    const priceForStripe = price * 100;
    const publishableKey = 'pk_test_qaRrnC4RhTRiVsZsn3uaiIYx00FYBecz6x'

    const onToken = token => {
    axios({
        url: 'payment',
        method: 'post',
        data: {
            amount: priceForStripe,
            token
        }
    }).then(response => {
        alert('Payment successful')
    }).catch(error => {
        console.log("Payment error:", JSON.parse(error))
        alert("There was an issue with your payment. Please make sure you provide a valid credit card.")
    })
}

    return (
        <StripeCheckout
        label='Pay Now'
        name='Better Practices Ltd.'
        billingAddress
        shippingAddress
        image='https://i-love-png.com/images/computer-hardware-notebook-icon.png'
        description={`Your total is $${price}`}
        amount={priceForStripe}
        panelLabel='Pay Now'
        token={onToken}
        stripeKey={publishableKey}
        />
    )
}

export default StripeCheckoutButton
