import React from 'react';
import './collection-item.styles.scss';
import CustomButton from '../custom-button/custom-button.component';
import {connect} from 'react-redux'
import addItem from '../../redux/cart/cart.actions'
import {selectCartItems} from '../../redux/cart/cart.selectors';
import {createStructuredSelector} from 'reselect'

const CollectionItem = ({item, cartItems, itemCount, dispatch}) => {
    const { name, price, imageUrl} = item;
    return (
    <div className="collection-item">
        <div
        className="image"
        style={{
            backgroundImage: `url(${imageUrl})`
        }}
        />
            <div className='collection-footer'>

            <span className='name'>{name}</span>
            <span className='price'>Q:{cartItems.map(cartItem => cartItem.quantity) }</span>

            </div>
            <CustomButton onClick={()=>dispatch(addItem(item))} inverted>Level Up</CustomButton>
        </div>
);
    }


    const mapStateToProps = createStructuredSelector ({
        cartItems: selectCartItems
    })

export default connect(mapStateToProps)(CollectionItem);
