import React from 'react';
import CollectionsOverviewContainer from '../../components/collections-overview/collections-overview.container'
import {Route} from 'react-router-dom';
import {createStructuredSelector} from 'reselect';
import CollectionPage from '../collection/collection.component';
import {connect} from 'react-redux'
import {fetchCollectionsStartAsync} from '../../redux/shop/shop.actions'
import withSpinner from '../../components/with-spinner/with-spinner.component';
import {selectIsCollectedLoaded} from '../../redux/shop/shop.selector';

const CollectionPageWithSpinner = withSpinner(CollectionPage)

class ShopPage extends React.Component {



    /// build the snapshot of collections pass in the coverter object to snapshot
    componentDidMount() {
        const {fetchCollectionsStartAsync} = this.props
        fetchCollectionsStartAsync()
    }

    render() {
        const {match, isCollectionLoaded} = this.props
    return (
    <div className="shop-page">
        <Route exact path={`${match.path}`} 
        component={CollectionsOverviewContainer}/>

        <Route path={`${match.path}/:collectionId`} 
        render={(props) => <CollectionPageWithSpinner 
        isLoading={!isCollectionLoaded} {...props} />} />
    </div>
)
} 
}

const mapStateToProps = createStructuredSelector({
    isCollectionLoaded: selectIsCollectedLoaded
})

const MapDispatchToProps = dispatch => ({
    fetchCollectionsStartAsync: ()=> dispatch(fetchCollectionsStartAsync())
})

export default connect(mapStateToProps, MapDispatchToProps)(ShopPage);
