import React from 'react';
import {Link} from 'react-router-dom';
import './header.styles.scss';
import {ReactComponent as Logo} from '../../assets/crown.svg'
import {auth} from '../../firebase/firebase.utils';
import {connect} from 'react-redux';
import CartIcon from '../cart-icon/cart-icon.component'
import CartDropDown from '../cart-dropdown/cart-dropdown.component';
import selectCurrentUser from '../../redux/user/user.selector'
import {selectCartHidden} from '../../redux/cart/cart.selectors'
import {createStructuredSelector} from 'reselect';
import {HeaderContainer, LogoContainer, OptionsContainer, OptionDiv, OptionsLink} from './header.styles';


const Header = ({currentUser, hidden}) => (
    <HeaderContainer>
        <LogoContainer to='/' >
        <Logo className='logo' />
        </LogoContainer>
        <OptionsContainer>
            <OptionsLink to="/shop">
                REQUEST
            </OptionsLink>
            <OptionsLink to="/contact">
                CONTACT
            </OptionsLink>
            {
                currentUser ?
                <OptionDiv onClick={()=> auth.signOut()}>
                    SIGN OUT
                </OptionDiv>
                :
                <OptionsLink className='option' to='/signin'>SIGN IN</OptionsLink>
            }
            <CartIcon />
        </OptionsContainer>
       {!hidden ? <CartDropDown /> : null}
    </HeaderContainer>
)

//current user is coming from the reducer 
const mapStateToProps = createStructuredSelector ({
    currentUser: selectCurrentUser,
    hidden: selectCartHidden
})

export default connect(mapStateToProps)(Header);
