import {createSelector} from 'reselect';

const selectCart = state => state.cart;

export const selectCartItems = createSelect(
    [selectCart],
    (cart) => cart.cartItems 
);

export const selectCartItemsCount = createSelector(
    [selectCartItems],
    cartItems => cartItems
    .reduce((acc, cartItem) => acc + 
    cartItem.quantity, 0
    )
);
